import merge from './merge.js';
import mergepairs from './mergepairs.js';
import decreasekey from './decreasekey.js';
import Node from './Node.js';

export default class PairingHeap {
	constructor(compare) {
		this.compare = compare; // Comparison function
		this.min = null; // Root node, must have .prev = .next = null at all times
	}

	/**
	 * Find-min: simply return the top element of the heap.
	 */
	head() {
		if (this.min === null) return undefined;
		return this.min.value;
	}

	headreference() {
		return this.min;
	}

	/**
	 * Delete-min: remove the root and merge its subtrees. Various strategies
	 * are employed.
	 */
	pop() {
		const min = this.popreference();
		return min === null ? undefined : min.value;
	}

	/**
	 */
	popreference() {
		if (this.min === null) return null;
		const min = this.min;
		this.min = mergepairs(this.compare, min.children); // Min.children.next = null
		return min;
	}

	/**
	 * Insert: create a new heap for the inserted element and merge into the
	 * original heap.
	 */
	push(value) {
		const node = new Node(value);
		return this.pushreference(node);
	}

	/**
	 * /!\ ref.next = ref.prev = null which means all references that are
	 * external to the tree must reset .next and .prev and one must not call
	 * PairingHeap#pushreference with an internal reference from this tree or
	 * another, except the root of another tree.
	 */
	pushreference(ref) {
		if (this.min === null) this.min = ref;
		else {
			// This.min != null != ref
			this.min = merge(this.compare, this.min, ref);
		}

		return ref;
	}

	/**
	 * Supposes the same comparison function is used in both trees.
	 * We can call pushreference since other.min.next = other.min.prev = null.
	 */
	merge(other) {
		const ref = other.min;
		if (ref !== null) this.pushreference(ref);
	}

	/**
	 * @param {Node} ref Non-null internal node object.
	 * @param {Object} value The new value for ref.
	 */
	update(ref, value) {
		const d = this.compare(value, ref.value);

		if (d < 0) this.decreasekey(ref, value);
		else if (d > 0) this.increasekey(ref, value);
		else ref.value = value;
	}

	/**
	 * Decrease-key
	 *
	 * @param {Node} ref Non-null internal node object.
	 * @param {Object} value The new value for ref.
	 */
	decreasekey(ref, value) {
		if (ref === this.min) ref.value = value;
		else {
			// This.min != null, ref != null
			this.min = decreasekey(this.compare, this.min, ref, value);
		}
	}

	/**
	 * Increase-key: remove the item at the key to be increased, replace
	 * the key with a larger key, then push the result back into the heap.
	 *
	 * @param {Node} ref Non-null internal node object.
	 * @param {Object} value The new value for ref.
	 *
	 */
	increasekey(ref, value) {
		this.delete(ref);

		ref.value = value;

		this.pushreference(ref);
	}

	/**
	 * Ref must be internal
	 * ref.prev and ref.next get reset to null
	 */
	delete(ref) {
		if (ref === this.min) {
			this.popreference();
			return;
		}

		const successor = mergepairs(this.compare, ref.children); // Ref.children.next = null

		// ref has no children
		if (successor === null) {
			//  _       _       _
			// | | --> | | --> | |
			// |_| <-- |_| <-- |_|
			//  P       R       N

			// detach ref and link ref.prev to ref.next
			//
			//  _               _
			// | | ----------> | |
			// |_| <    _   -> |_|
			//  P   \  | | /  / N
			//       - |_| <-/
			//          R
			//
			ref.prev.next = ref.next; // Must be != null because ref != min

			if (ref.next !== null) {
				//
				//  _               _
				// | | ----------> | |
				// |_| <---------- |_|
				//  P       _       N
				//  ^      | | -----^
				//  |----- |_|
				//          R
				//
				ref.next.prev = ref.prev;
				//
				//  _               _
				// | | ----------> | |
				// |_| <---------- |_|
				//  P       _       N
				//  ^      | |
				//  |----- |_|
				//          R
				//
				ref.next = null;
			}

			//
			//  _               _
			// | | ----------> | |
			// |_| <---------- |_|
			//  P       _       N
			//         | |
			//         |_|
			//          R
			//
			ref.prev = null;

			return;
		}

		successor.prev = ref.prev; // Must be != null because ref != min
		successor.prev.next = successor;
		ref.prev = null;

		if (ref.next !== null) {
			successor.next = ref.next; // Might be null
			successor.next.prev = successor;
			ref.next = null;
		}
	}
}

import merge from './merge.js';

/**
 * Decrease-key: remove the subtree rooted at the key to be decreased, replace
 * the key with a smaller key, then merge the result back into the heap.
 *
 * @param {Function} compare Comparison function for keys.
 * @param {Node} min Current minimum-key node != null with .prev = .next = null.
 * @param {Node} node Node != null to update with .prev != null.
 * @param {Object} value The new value for the key of the node to update.
 * @returns {Node} Returns the node containing the minimum key.
 */
export default function decreasekey(compare, min, node, value) {
	// Update node's key
	node.value = value;

	// Remove node from tree
	node.prev.next = node.next; // By assumption node.prev != null
	if (node.next !== null) {
		node.next.prev = node.prev;
		node.next = null;
	}

	node.prev = null;

	// Merge, remember we move the whole subtree with children
	// node.prev = node.next = null at this point so safe to call merge
	// min != null and node != null
	return merge(compare, min, node);
}

export {default as Node} from './Node.js';
export {default as PairingHeap} from './PairingHeap.js';
export {default as decreasekey} from './decreasekey.js';
export {default as merge} from './merge.js';
export {default as mergepairs} from './mergepairs.js';
export {default as prepend} from './prepend.js';

import prepend from './prepend.js';

/**
 * Merge: compare the two root elements, the smaller remains the root of the
 * result, the larger element and its subtree is appended as a child of this
 * root.
 *
 * /!\ Precondition:
 *   1. A != null and A.next = A.prev = null
 *   2. B != null and B.next = B.prev = null
 *
 * @param {Function} compare Comparison functions for keys.
 * @param {Node} A The first input node.
 * @param {Node} B The second input node.
 * @returns {Node} The input node with smallest key with .next = .prev = null.
 */
export default function merge(compare, A, B) {
	if (compare(A.value, B.value) < 0) return prepend(A, B);

	return prepend(B, A);
}

import merge from './merge.js';

/**
 * Recursively builds a heap from an iterator of nodes by merging them pair by
 * pair.
 *
 * @param {Function} compare Comparison function for node keys.
 * @param {Node} prev Last node before first of list. First node is `prev.next`.
 * @returns {Node} The root node with .next = .prev = null or null for an empty
 *                 iterator.
 */
export default function mergepairs(compare, previous) {
	// Unpick linked list starting at prev.next

	const A = previous.next;
	previous.next = null;

	if (A === null) return null;
	A.prev = null;

	const B = A.next;
	A.next = null;

	if (B === null) return A;
	B.prev = null;

	// Recursion fairy for the rest of the heap
	const tail = mergepairs(compare, B); // Sets B.next = null

	// merge A != null with B != null
	const head = merge(compare, A, B); // Call to merge is valid

	// merge with the rest
	if (tail === null) return head;

	// Head != null, tail != null
	return merge(compare, head, tail); // Also valid because tail and head
	// are outputs of merge{pairs}
}

/**
 * Set B as the first child of A.
 *
 * /!\ Precondition:
 *   1. A != null
 *   2. B != null
 *   3. A.next = A.prev = B.next = B.prev = null
 *
 * @param {Node} A
 * @param {Node} B
 * @return {Node} The input node A with .next = .prev = null.
 */
export default function prepend(A, B) {
	B.prev = A.children;
	B.next = A.children.next;
	A.children.next = B;

	if (B.next !== null) B.next.prev = B;

	return A;
}

